mapper.select:

SELECT bno, bname, btitle, bcontent, bregdate, breadcount
FROM (
		SELECT /*+INDEX_DESC(tbl_board pk_board) */
			ROWNUM rn, bno, bname, btitle, bcontent, bregdate, breadcount
		FROM tbl_board
		WHERE ROWNUM <= #{pageNum} * #{amount}
)
WHERE rn > (#{pageNum} - 1) * #{amount}

SELECT bno, bname, btitle, bcontent, bregdate, breadcount
FROM tbl_board
WHERE bno = #{bno}

mapper.update:
UPDATE tbl_board
SET bname = #{bname},
	btitle = #{btitle},
	bcontent = #{bcontent},
	breadcount = #{breadcount}
WHERE bno = #{bno}

UPDATE tbl_board
SET breadcount = (breadcount + 1)
WHERE bno = #{bno}

mapper.select:
SELECT fno, bno, fname, ffolder, uuid
FROM tbl_file
WHERE bno = #{bno}
